/**
 * This is the alt instance that holds the state of the entire application.
 * All stores and actions are created on this instance.
 */

import Alt from 'alt';

let instance;

setAltInstance(new Alt());

export function setAltInstance(alt) {
    if (instance) teardown(instance);
    setup(alt);
}
export function getAltInstance() {
    return instance;
}

const root = global || window;

function setup(alt) {
    instance = alt;
    if (process.env.NODE_ENV === 'development') {
        // Debugging with chrome devtools
        // @see https://github.com/goatslacker/alt-devtool
        require('alt-utils/lib/chromeDebug')(instance);

        // sometimes, chromeDebug just doesn't update until an action is dispatched.
        // we use a dummy action to do just that
        const refreshAction = instance.generateActions('__refresh__').__refresh__;
        instance.handleMessage = e => {
            if (e.data && e.data.type === 'ALT' && e.data.source === 'alt-devtools') {
                refreshAction.defer();
            }
        };
        root && root.addEventListener && root.addEventListener('message', instance.handleMessage);
    }
}
function teardown() {
    if (process.env.NODE_ENV === 'development') {
        root && root.removeEventListener && root.removeEventListener('message', instance.handleMessage);
    }
}

import { bind as bindAlt } from 'alt-utils/lib/decorators';

// DEPRECATED
// replace with "calls and viewActions!"

/**
 * Creates a decorator for binding action handlers to a store.
 * A binding is created for each action that has a matching handler method mapping.
 *
 * The decorator takes any number of action handler definitions as arguments and applies their
 * bindings after concatinating them to a flat list, so you can pass either of these:
 * - an array of handler definitions
 * - any number of array of handler definitions
 * - any number of handlerdefinitions without arrays
 * - any mixed variation
 *
 * See manual/usage/action-handlers.md for more on this topic.
 */
export default function BindHandlers(...args) {
    const definitions = args.reduce((result, def) => {
        if (Array.isArray(def)) {
            def.forEach(d => result.push(d));
        } else {
            result.push(def);
        }
        return result;
    }, []);
    return function decorateStore(StoreClass) {
        definitions.forEach(function bindActions({ handler, bindings }, i) {
            if (!handler || !handler.prototype) {
                throw new Error('Invalid action handler');
            }
            // we need unique prefixes for the potentionally same method names
            // using handler.prototype.constructor.name alone is useless after mangling/uglifying!
            const name = `$${i}_${handler.prototype.constructor.name}`;
            // collect the names of the methods defined in the handler decorator
            const methodNames = Object.keys(bindings);
            // now for each decorator method that has an action by the same name...
            methodNames.forEach(function bindAction(methodName) {
                if (typeof bindings[methodName] !== 'function') {
                    throw new Error(`bindings.${methodName} is not a function (handler: ${name})`);
                }
                if (typeof handler.prototype[methodName] !== 'function') {
                    throw new Error(`${name}.${methodName} is not a function`);
                }
                const storeMethodName = `${name}_${methodName}`;
                // ...copy the method from the decorator class to the store class
                StoreClass.prototype[storeMethodName] = handler.prototype[methodName];
                // and bind the action to it, using Alt's 'bind' util
                const applyBinding = bindAlt(bindings[methodName]);
                applyBinding(
                    StoreClass,
                    storeMethodName,
                    Object.getOwnPropertyDescriptor(StoreClass.prototype, storeMethodName)
                );
            });
        });
        return StoreClass;
    };
}

import { validateCreator, validateDefinition } from './utils/validate';
import { createLogger } from './utils/logging';
import createActions from './createActions';

export default function callFactory(
    name,
    {
        namespace = 'global',
        defaultActions = ['loading', 'error', 'success'],
        actions = createActions(`${namespace}:${name}`, defaultActions),
        logger = createLogger(`${namespace}:${name}`)
    } = {}
) {
    const errors = validateCreator({ name, actions, logger }, logger);
    const errorKeys = errors && Object.keys(errors);
    if (errorKeys && errorKeys.length) {
        throw new Error(errors[errorKeys[0]]);
    }

    return {
        name,
        actions,

        create: definition => {
            const createDefinition = typeof definition === 'function' ? definition : () => definition;
            const call = Object.assign(createDefinition({ name, actions, logger }), {
                name,
                actions,
                logger
            });
            if (validateDefinition(call, logger)) {
                throw new Error('Invalid call');
            }
            call.dataSource = {
                // set the default actions
                ...(call.actions || {}),
                // set dataSource from the definition passed om - potentially overriding the actions
                ...(call.dataSource || {})
            };
            return call;
        },
        createActions: actionNames => createActions(`${namespace}:${name}`, actionNames)
    };
}

/**
 * Returns a promise and executes a series of functions that each return a promise.
 * Uses an initial 0-timeout so that we don't run into "Cannot dispatch in the middle of a dispatch" errors in case
 * one of the calls triggers a `loading` action
 *
 * @example
 * import {callSeries} from '@xailabs/altx';
 * const calls = [
 *      () => fetch('foo'),
 *      () => fetch('bar'),
 *      () => new Promise((resolve) => setTimeout(resolve, 1000)),
 * ];
 * callSeries(calls).then(console.log)
 *
 * @param {array} calls - An array of function that each returns a promise
 * @param {object} options - An object with additional options
 * @param {boolean} options.log - Whether to log each individual call to the console
 * @return {promise} - A promise that will be resolved when all calls succeeded or rejected if at least one call failed
 */
export default function callSeries(calls, { log = false } = {}) {
    return new Promise((resolve, reject) => {
        let results = [];
        setTimeout(() => {
            function series(list) {
                log && console.log('[callSeries] list', list);
                var p = Promise.resolve();
                return list.reduce(function(pacc, fn) {
                    return (pacc = pacc.then(res => {
                        log && console.log('[callSeries] res', res);
                        results.push(res);
                        return fn(res);
                    }));
                }, p);
            }
            series(calls)
                .then(() => setTimeout(() => resolve(results)))
                .catch(err => reject(err));
        });
    });
}

