import createActions from './createActions';

/**
 * Creates a single action with a namespace.
 * Basically just like `altx.generateActions`, but with a signle required argument `name` and an optional second argument `namespace`.
 *
 * Note: The returned object is very much the same as with the plural version `createActions`: You get back an object that has
 * a function by the name you gave as well as an constant for the action name.
 *
 * @example
 * createAction('myAction', 'myNamespace'); // {name: 'myNamespace', myAction: function() {...}, MY_ACTION: 'myNamespace.myAction'}
 *
 * @param {string} name - The name to use for the action
 * @param {string} namespace - The namespace to use for the action
 * @return {object} An name object
 */
export default function createAction(name, namespace = 'default') {
    const actions = createActions(namespace, [name]);
    return actions;
}

import { getAltInstance } from './altInstance';

/**
 * Creates simple (data-pass-through) actions with a namespace.
 * Basically just like `alt.generateActions`, but within a given namespace instead of 'global'.
 *
 * @param {string} namespace - The namespace to use for the actions
 * @param {array<string>} actions - An array of action names
 * @return {object} An object containing the generated actions and constants
 */
export default function createActions(namespace, actions) {
    return getAltInstance().createActions({
        name: namespace,
        ...[].concat(actions).reduce((result, action) => {
            result[action] = (data = {}) => data;
            return result;
        }, {})
    });
}

import Immutable from 'immutable';
import { decorate, datasource } from 'alt-utils/lib/decorators';
import immutable from 'alt-utils/lib/ImmutableUtil';

import { getAltInstance } from './altInstance';
import ImmutableStore from './ImmutableStore';
import getSources from './getSources';

import bindCalls from './decorators/bindCalls';
import bindActions from './decorators/bindActions';

export default function createStore(displayName, { alt, state, calls, sources, viewActions } = {}) {
    if (!displayName) {
        throw new Error('displayName is required');
    }

    alt = alt || getAltInstance();
    state = state || Immutable.fromJS({});

    calls = calls || [];
    sources = sources || getSources(calls);
    viewActions = viewActions || [];

    return alt.createStore(
        @decorate(alt)
        @datasource(sources)
        @bindCalls(calls)
        @bindActions(viewActions)
        @immutable
        class extends ImmutableStore {
            constructor() {
                super(state);
            }
        },
        displayName
    );
}

import { bind } from 'alt-utils/lib/decorators';
import flatten from '../utils/flatten';
import { getLevel as getLogLevel, logLevel } from '../utils/logging';

/**
 * Decorates a store with any number of viewAction definitions.
 */
export default function bindActions(...args) {
    return function decorate(storeClass) {
        const calls = flatten(args);
        calls.forEach(call => bindViewActionHandler(storeClass, call));
        return storeClass;
    };
}

function bindViewActionHandler(storeClass, definition) {
    const { name, action, reducer, sideEffect, logger, logging } = definition;

    const handlerName = `${name}`;

    if (storeClass.prototype[handlerName]) throw new Error(`Duplicate handler "${handlerName}"`);

    storeClass.prototype[handlerName] = function handleViewAction(payload) {
        if (logging || getLogLevel() === logLevel.FORCE) {
            logger[payload instanceof Error ? 'error' : 'log'](payload && payload.toJS ? payload.toJS() : payload);
        }

        const currentState = this.state;
        let nextState = currentState;

        try {
            nextState = reducer && reducer(currentState, payload);
        } catch (error) {
            console.error(`${handlerName}: error executing reducer`, error);
        }

        if (nextState && nextState !== currentState) {
            this.setState(nextState);
        }

        if (sideEffect) {
            setTimeout(() => {
                try {
                    sideEffect({ state: nextState, prevState: currentState, payload });
                } catch (error) {
                    console.error(`${handlerName}: error executing sideEffect`, error);
                }
            });
        }
    };

    const bindActionHandler = bind(action);

    bindActionHandler(storeClass, handlerName, Object.getOwnPropertyDescriptor(storeClass.prototype, handlerName));
}

import { bind } from 'alt-utils/lib/decorators';
import flatten from '../utils/flatten';
import { getLevel as getLogLevel, logLevel } from '../utils/logging';
/**
 * Decorates a store with any number of call definitions.
 */
export default function bindCalls(...args) {
    return function decorate(storeClass) {
        const calls = flatten(args);
        calls.forEach(call => decorateStoreWithCall(storeClass, call));
        return storeClass;
    };
}

/**
 * Decorates a store with a single call definition.
 * Attaches the dataSource specified in the call definition using alt's datasource decorator.
 * Creates and binds a handler function for all reducers and actions specified in the call definition.
 */
function decorateStoreWithCall(storeClass, callDefinition) {
    const actionNames = Object.keys(callDefinition.actions).reduce((result, key) => {
        // remove ACTION_CONSTANT variants generated by alt
        if (result.indexOf(key.toLowerCase()) === -1) {
            result.push(key);
        }
        return result;
    }, []);
    actionNames.forEach(reducerName => {
        bindReducerHandler(reducerName, storeClass, callDefinition);
    });
}

/**
 * Attaches a single reducer handling to the store.
 * A new handler method will be created on the store for each action associated
 * with a reducer (defaults to the action names: loading, error, success). Each handler will pass
 * the current state and the action payload to the reducer with the same name
 * and mutate the store with the new state returned by the reducer.
 * Any sideEffects defined in the call will be executed with a ({state, prevState, payload}) signature.
 */
function bindReducerHandler(reducerName, storeClass, callDefinition) {
    const handlerName = `_${callDefinition.name}_${reducerName}`;

    if (storeClass.prototype[handlerName]) throw new Error(`Duplicate handler "${handlerName}"`);

    storeClass.prototype[handlerName] = function handleCallAction(payload) {
        const reducer = callDefinition.hasOwnProperty('reducers') && callDefinition.reducers[reducerName];
        const sideEffect = callDefinition.hasOwnProperty('sideEffects') && callDefinition.sideEffects[reducerName];
        const logging = callDefinition.hasOwnProperty('logging') && callDefinition.logging;
        const logger = callDefinition.hasOwnProperty('logger') && callDefinition.logger;
        const isError = payload instanceof Error;
        if (isError) {
            if (payload.response && payload.response.body && payload.response.body.message) {
                logger.error(reducerName, payload.response.body.message);
            }
            // logger.debug(payload);
        } else if (logging || getLogLevel() === logLevel.FORCE) {
            logger[isError ? 'error' : 'log'](reducerName, payload && payload.toJS ? payload.toJS() : payload);
        }

        const currentState = this.state;
        let nextState = currentState;

        if (reducer) {
            //console.log(`[${handlerName}]`, payload, callDefinition);
            try {
                nextState = reducer(currentState, payload);
            } catch (error) {
                console.error(`Error in reducer (${callDefinition.name}, ${reducerName})`, error);
            }
        }

        if (reducer && !nextState)
            console.warn(
                `reducer "${reducerName}" in call "${
                    callDefinition.name
                }" did not return a new state. Either you forgot to return it, or you should consider using a sideEffect instead of a reducer if no return value is needed.`
            );

        if (nextState) {
            this.setState(nextState);
        }

        if (sideEffect) {
            setTimeout(() => {
                try {
                    sideEffect({ state: nextState, prevState: currentState, payload });
                } catch (error) {
                    console.error(`Error in sideEffect (${callDefinition.name}, ${reducerName})`, error);
                }
            });
        }
    };

    const action = callDefinition.actions[reducerName];
    const bindActionHandler = bind(action);

    bindActionHandler(storeClass, handlerName, Object.getOwnPropertyDescriptor(storeClass.prototype, handlerName));
}

import { bind } from 'alt-utils/lib/decorators';
import flattenArrays from '../utils/flatten';

// TODO clean up and refactor terminoloy, potentially deprecate the old "handlers" system
// (viewActions are now called handlers, e.g. handlerFactory)

/**
 * Decorates a store with any number of action handlers.
 */
export default function bindHandlers(actions, ...handlers) {
    return function decorateStore(storeClass) {
        // attach bound handler methods to the store class for each handler definition
        flattenArrays(handlers).forEach(handler => attachBoundHandler(storeClass, actions[handler.name], handler));
        return storeClass;
    };
}
/**
 * Attaches a single reducer handling to the store.
 *
 * A new handler method will be created on the store class.
 * The handler method invokes the reducer giving it the current store state.
 * It then sets the reducer result as the new store state.
 *
 * If a sideEffect function is defined, it will be called with the signature `{state, prevState, payload}`
 *
 * @param {object} storeClass - The class of the store to be decorated
 * @param {object} action - An altjs action
 * @param {object} handler - An handler object with `{name[, reducer, sideEffect]}`
 */
function attachBoundHandler(storeClass, action, handler) {
    // name is required

    const methodName = `__handle_${handler.name}`;

    if (storeClass.prototype[methodName]) throw new Error(`Duplicate method "${methodName}"`);

    /**
     * Handles an action call and sets the next state of the store.
     *
     * @param {any} payload - the single argument that can be specified when calling an action.
     *                      If you need to use more than one argument, use an object with any properties you need.
     */
    storeClass.prototype[methodName] = function handleAction(payload) {
        const reducer = handler.hasOwnProperty('reducer') && handler.reducer;
        const sideEffect = handler.hasOwnProperty('sideEffect') && handler.sideEffect;

        const currentState = this.state;

        // the actual operation: run the reducer and set its result as state
        let nextState = currentState;
        if (reducer) {
            try {
                nextState = reducer(currentState, payload);
            } catch (error) {
                console.error(`Error in reducer (${handler.name}, ${handler.name})`, error);
            }
        }
        if (nextState) {
            this.setState(nextState);
        } else if (reducer) {
            console.warn(`reducer "${handler.name}" in call "${handler.name}" did not return a new state.
                Either you forgot to return it, or if no state change is required, maybe you should use a sideEffect instead of a reducer.
            `);
        }

        if (sideEffect) {
            try {
                sideEffect({ state: nextState, prevState: currentState, payload });
            } catch (error) {
                console.error(`Error in sideEffect (${handler.name}, ${handler.name})`, error);
            }
        }
    };

    const bindhandler = bind(action);

    bindhandler(storeClass, methodName, Object.getOwnPropertyDescriptor(storeClass.prototype, methodName));
}

import Immutable from 'immutable';
import Events from '@loopmode/events';

/**
 * Decorator for ImmutableStore to persist values across browser refresh.
 *
 * Supports `window.localStorage` and `window.sessionStorage` as well as `localForage` or any other backend that implements the [Web Storage API](https://developer.mozilla.org/en-US/docs/Web/API/Web_Storage_API).
 * The storage backend may return promises for asynchronous usage.
 *
 * To store only specific parts of the state, provide a `keyPaths` array. Omit `keyPaths` to persist the entire state of the store.
 *
 * @param {Object} options
 * @param {Array<Array|String>} [options.keyPaths] - An array of keyPaths. Each keyPath can be an array of strings as used in Immutable.js, e.g. `['foo', 'bar']` or a string with dot delimiters, e.g. `'foo.bar'`
 * @param {Object} [options.storage=window.localStorage] - An object that supports the [Web Storage API](https://developer.mozilla.org/en-US/docs/Web/API/Web_Storage_API) methods
 * @param {Function} [options.parse] - A function that receives the persisted value and returns the data object. Defaults to `JSON.parse` if `storage` is an instance of `window.Storage`
 * @param {Function} [options.encode] - A function that receives the data object and returns the value to be persisted. Defaults to `JSON.stringify` if `storage` is an instance of `window.Storage`
 * @param {Boolean} [options.logging] - Whether to log details to the console during operations
 * @return {Function} - The decorator function for the target store
 */
export default function bindStorage({ storage = window.localStorage, keyPaths, parse, encode, logging } = {}) {
    if (storage instanceof window.Storage) {
        parse = parse || JSON.parse;
        encode = encode || JSON.stringify;
    }
    return function decorate(StoreClass) {
        return class PersistableStore extends StoreClass {
            constructor(model) {
                super(model);
                this.persistenceEnable({ storage, keyPaths });
                this.exportPublicMethods({
                    persistenceEnable: () => this.persistenceEnable({ storage, keyPaths }),
                    persistenceDisable: () => this.persistenceDisable()
                });
            }

            persistenceEnable = ({ storage, keyPaths }) => {
                this.persistenceConfig = {
                    storage,
                    name: `persisted.${this.displayName}`,
                    keyPaths:
                        keyPaths &&
                        keyPaths.map(keyPath => {
                            if (typeof keyPath === 'string') return keyPath.split('.');
                            return keyPath;
                        })
                };

                if (logging) {
                    console.log('[PersistableStore] persistenceEnable', { store: this });
                }

                Events.on('beforeunload', this.persistenceSave);

                this.persistenceRestore();
            };
            persistenceDisable = () => {
                if (logging) {
                    console.log('[PersistableStore] persistenceDisable', { store: this });
                }

                Events.off('beforeunload', this.persistenceSave);
            };
            persistenceRestore = async () => {
                try {
                    const { storage, name } = this.persistenceConfig;
                    let data = await storage.getItem(name);
                    if (data) {
                        if (parse) data = parse(data);

                        data = Immutable.fromJS(data);
                        this.persistenceExtendInitialData(data);

                        const nextState = this.state.mergeDeep(data);

                        if (logging) {
                            console.log('[PersistableStore] persistenceRestore', { store: this, data, nextState });
                        }

                        this.setState(nextState);
                    }
                } catch (error) {
                    console.warn('[PersistableStore] persistenceRestore failed', { error, store: this });
                }
            };
            persistenceSave = async () => {
                try {
                    const { storage, name, keyPaths } = this.persistenceConfig;
                    let data = this.state;

                    if (keyPaths && keyPaths.length) {
                        data = keyPaths.reduce(
                            (result, keyPath) => result.setIn(keyPath, this.state.getIn(keyPath)),
                            Immutable.fromJS({})
                        );
                    }

                    data = data.toJS();

                    if (encode) data = encode(data);

                    if (logging) {
                        console.log('[PersistableStore] persistenceRestore', { store: this, data });
                    }

                    await storage.setItem(name, data);
                } catch (error) {
                    console.warn('[PersistableStore] persistenceSave failed', { error, store: this });
                }
            };
            persistenceExtendInitialData = persistedData => {
                if (!this.initialData) {
                    return;
                }
                const wasImmutable = typeof this.initialData.toJS === 'function';
                this.initialData = Immutable.fromJS(this.initialData).mergeDeep(persistedData);
                if (!wasImmutable) {
                    this.initialData = this.initialData.toJS();
                }
            };
        };
    };
}

import connectToStores from 'alt-utils/lib/connectToStores';
import React, { createElement } from 'react';

// TODO: deprecate connectAlternative asap!

/* eslint-disable */
/**
 * A component decorator for connecting to immutable stores.
 *
 * Basically a wrapper around `alt/utils/connectToStores`.  
 * Adds the necessary static methods `getStores()` and `getPropsFromStores()` to the decorated component.
 *
 * - Supports multiple stores.
 * - Supports a simplified, string-based access to stores, with optional renaming of props.
 * - Supports more flexible, redux-like access to stores using mapper functions.
 *
 * ### String notation
 *
 * @example
 * @connect([{store: MyStore, props: ['myValue', 'anotherValue']}])
 * export default class MyComponent extends React.Component {
 *      render() {
 *          const {myValue, anotherValue} = this.props;
 *          ...
 *      }
 * }
 *
 * You can rename props using the ` as ` alias syntax
 *
 * @example
 * @connect([{
 *      store: PeopleStore,
 *      props: ['items as people']
 * }, {
 *      store: ProductStore,
 *      props: ['items as products']
 * }])
 * export default class MyComponent extends React.Component {
 *      render() {
 *          // this.props.people, this.props.products, ...
 *      }
 * }
 *
 * ### Function notation
 *
 * Use mapper functions instead of strings in order to manually retrieve store values.
 * The function receives the store state and the component props.
 *
 * @example
 * @connect([{
 *      store: MyStore,
 *      props: (state, props) => {
 *          return {
 *              item: state.get('items').filter(item => item.get('id') === props.id)
 *          }
 *      }
 * }])
 * export default class MyComponent extends React.Component {
 *      render() {
 *          const item = this.props.item;
 *      }
 * }
 *
 * Technically, you could also mix all access methods, but this defeats the purpose of simple access:
 *
 * @example
 * @connect([{
 *      store: MyStore,
 *      props: ['someProp', 'anotherProp', (state, props) => {
 *          return {
 *              item: state.get('items').filter(item => item.get('id') === props.id)
 *          }
 *      }, 'some.nested.value as foo']
 * }])
 * export default class MyComponent extends React.Component {
 *      ...
 * }
 *
 * There are however valid usecase for mixing access methods. For example, you might have keys that themselves contain dots.
 * For example, that is the case when using `validate.js` with nested constraints and keeping validation results in the store.
 * There might be an `errors` map in your storewith keys like `user.address.street`. In such a case you wouldn't be able to access those values because the dots do not
 * represent the actual keyPath in the tree:
 *
 * @example
 * @connect([{
 *   store,
 *   props: ['user.address.street', (state) => ({errors: state.getIn(['errors', 'user.address.street'])})]
 * }])
 *
 * @see https://github.com/goatslacker/alt/blob/master/docs/utils/immutable.md
 * @see https://github.com/goatslacker/alt/blob/master/src/utils/connectToStores.js
 *
 * @param {Array<{store: AltStore, props: Array<string>}>} definitions - A list of objects that each define a store connection
 */
/* eslint-enable */
export default function connect(definitions) {
    return function(targetClass) {
        targetClass.getStores = function() {
            return definitions.map(def => def.store);
        };
        targetClass.getPropsFromStores = function(componentProps) {
            return definitions.reduce((result, def) => {
                if (typeof def.props === 'function') {
                    // the props definition is itself a function. return with its result.
                    return Object.assign(result, def.props(def.store.state, componentProps));
                }
                // the props definition is an array. evaluate and reduce each of its elements
                return def.props.reduce((result, accessor) => {
                    return Object.assign(result, mapProps(accessor, def.store.state, componentProps));
                }, result);
            }, {});
        };
        return connectToStores(targetClass);
    };
}

function mapProps(accessor, state, props) {
    switch (typeof accessor) {
        case 'function':
            return mapFuncAccessor(accessor, state, props);
        case 'string':
            return mapStringAccessor(accessor, state);
    }
}

function mapFuncAccessor(accessor, state, props) {
    return accessor(state, props);
}

function mapStringAccessor(accessor, state) {
    const { keyPath, propName } = parseAccessor(accessor);
    return {
        [propName]: state.getIn(keyPath)
    };
}

/**
 * Takes the accessor defined by the component and retrieves `keyPath` and `propName`
 * The accessor may be the name of a top-level value in the store, or a path to a nested value.
 * Nested values can be accessed using a dot-separated syntax (e.g. `some.nested.value`).
 *
 * The name of the prop received by the component is the last part of the accessor in case of
 * a nested syntax, or the accessor itself in case of a simple top-level accessor.
 *
 * If you need to pass the value using a different prop name, you can use the ` as ` alias syntax,
 * e.g. `someProp as myProp` or `some.prop as myProp`.
 *
 * examples:
 *
 *      'someValue' // {keyPath: ['someValue'], propName: 'someValue'}
 *      'someValue as foo' // {keyPath: ['someValue'], propName: 'foo'}
 *      'some.nested.value' // {keyPath: ['some', 'nested', 'value'], propName: 'value'}
 *      'some.nested.value as foo' // {keyPath: ['some', 'nested', 'value'], propName: 'foo'}
 *
 * @param {string} string - The value accessor passed by the component decorator.
 * @return {object} result - A `{storeName, propName}` object
 * @return {string} result.keyPath - An immutablejs keyPath array to the value in the store
 * @return {string} result.propName - name for the prop as expected by the component
 */
function parseAccessor(accessor) {
    let keyPath, propName;
    if (accessor.indexOf(' as ') > -1) {
        // e.g. 'foo as bar' or 'some.foo as bar'
        const parts = accessor.split(' as ');
        keyPath = parts[0].split('.');
        propName = parts[1];
    } else {
        // e.g. 'foo' or 'some.foo'
        keyPath = accessor.split('.');
        propName = keyPath[keyPath.length - 1];
    }
    return { keyPath, propName };
}

function connectAlternative(store, mapStateToProps, WrappedComponent) {
    return class Connect extends React.Component {
        constructor(props, context) {
            super(props, context);
            const storeState = store.getState();
            this.state = { storeState: mapStateToProps(storeState, props) };
        }

        componentDidMount() {
            this._isMounted = true;
            this.storeSubscription = store.listen(this.handleStoreUpdate);
        }

        componentWillUnmount() {
            this._isMounted = false;
            if (this.storeSubscription) {
                store.unlisten(this.storeSubscription);
            }
        }

        // if we use props in mapStateToProps,
        // we need to run it again when props have changed
        componentWillReceiveProps(nextProps) {
            //untested! should work though
            if (mapStateToProps.length > 1) {
                this.setState({ storeState: mapStateToProps(store.getState(), nextProps) });
            }
        }

        handleStoreUpdate = state => {
            if (this._isMounted) {
                this.setState({ storeState: mapStateToProps(state, this.props) });
            }
        };

        render() {
            const mergedProps = { ...this.props, ...this.state.storeState };
            return createElement(WrappedComponent, mergedProps);
        }
    };
}

export { connectAlternative };

