}

export function createLogger(name) {
    return {
        log: (...args) => level > NONE && console.log(`[${name}]`, ...args),
        info: (...args) => level > NONE && console.info(`[${name}]`, ...args),
        warn: (...args) => level > NONE && console.warn(`[${name}]`, ...args),
